import { publishStatuses } from '@/common/data/data';
import { formatDateTime, getImageUrl } from '@/common/utils/utils';
import { DeleteOutlined, EditOutlined, PlusOutlined } from '@ant-design/icons';
import {
  ActionType,
  FooterToolbar,
  PageContainer,
  ProColumns,
  ProTable,
} from '@ant-design/pro-components';
import { Button, Image, Modal, message } from 'antd';
import React, { useRef, useState } from 'react';
import InformationForm from './components/InformationForm';
import { InformationTableItem } from './data/data';
import { deleteInformation, getInformationDataTable } from './data/services/service';
// import UserForm from './components/UserForm';
// import { deleteUser, getUser } from './data/services/service';

/**
 *  Delete node
 * @zh-CN 删除节点
 *
 * @param selectedRows
 */
const handleRemove = async (selectedRow: UserFeature.UserListItem | undefined) => {
  const hide = message.loading('Mohon Tunggu');
  if (!selectedRow) return true;
  try {
    await deleteInformation(selectedRow.id);
    hide();
    message.success('Deleted successfully and will refresh soon');
    return true;
  } catch (error: any) {
    hide();
    let errorMessage: string | undefined = error.response?.data?.message;
    if (errorMessage) {
      message.error(errorMessage);
      return false;
    }
    message.error('Delete failed, please try again');
    return false;
  }
};

const InformationPage: React.FC = () => {
  const [currentRow, setCurrentRow] = useState<UserFeature.UserListItem | undefined>();
  const [createModalOpen, handleModalOpen] = useState<boolean>(false);
  const [selectedRowsState, setSelectedRows] = useState<UserFeature.UserListItem[]>([]);
  const [deleteModalOpen, handleDeleteModalOpen] = useState<boolean>(false);

  const actionRef = useRef<ActionType>();

  const columns: ProColumns<InformationTableItem>[] = [
    {
      title: 'Judul',
      dataIndex: 'title',
    },
    {
      title: 'Tanggal',
      dataIndex: 'created_at',
      render: (data: any, _) => {
        return formatDateTime(data, 'DD/MM/YYYY');
      },
    },

    {
      title: 'Gambar',
      dataIndex: 'image',
      render: (_, record) => {
        const image = record.image;
        return (
          <Image
            src={getImageUrl(image?.thumbnail) || ''}
            style={{ width: 40, height: 40, objectFit: 'cover' }}
            fallback="data:image/png;base64,"
            preview={{
              src: getImageUrl(image?.address) || '',
            }}
          />
        );
      },
    },

    {
      title: 'Status',
      dataIndex: 'status',
      valueEnum: publishStatuses,
    },

    {
      title: 'Dibuat Oleh',
      dataIndex: ['created_by', 'name'],
      search: false,
    },

    {
      title: 'Aksi',
      dataIndex: 'option',
      width: '15%',
      valueType: 'option',
      render: (_, record) => [
        <a
          key="edit"
          onClick={() => {
            console.log(record);
            handleModalOpen(true);
            setCurrentRow(record);
          }}
        >
          <EditOutlined /> Edit
        </a>,
        <a
          key="delete"
          onClick={() => {
            handleDeleteModalOpen(true);
            setCurrentRow(record);
          }}
        >
          <DeleteOutlined /> Hapus
        </a>,
      ],
    },
  ];
  return (
    <PageContainer>
      <ProTable<InformationTableItem, API.PageParams>
        headerTitle="Daftar Informasi"
        rowKey="id"
        actionRef={actionRef}
        search={{
          labelWidth: 200,
        }}
        request={getInformationDataTable}
        columns={columns}
        rowSelection={{
          onChange: (_, selectedRows) => {
            setSelectedRows(selectedRows);
          },
        }}
        toolBarRender={() => [
          <Button
            type="primary"
            key="primary"
            onClick={() => {
              setCurrentRow(undefined);
              handleModalOpen(true);
            }}
          >
            <PlusOutlined /> Tambah
          </Button>,
        ]}
      />
      {selectedRowsState?.length > 0 && (
        <FooterToolbar
          extra={
            <div>
              Dipilih <a style={{ fontWeight: 600 }}>{selectedRowsState.length}</a> Item
              &nbsp;&nbsp;
            </div>
          }
        >
          <Button
            onClick={async () => {
              // await handleRemove(selectedRowsState);
              setSelectedRows([]);
              actionRef.current?.reloadAndRest?.();
            }}
          >
            Batch Deletion
          </Button>
        </FooterToolbar>
      )}

      <InformationForm
        open={createModalOpen}
        setOpen={handleModalOpen}
        values={currentRow}
        onSubmit={async (a) => {
          message.success('Berhasil mengupdate data');
          setTimeout(() => {
            window.location.reload();
          }, 1500);
          actionRef.current?.reloadAndRest?.();
          return true;
        }}
        onCancel={(a) => {}}
      />

      <Modal
        title="Confirm Delete"
        open={deleteModalOpen}
        onCancel={() => handleDeleteModalOpen(false)}
        onOk={async () => {
          await handleRemove(currentRow);
          if (actionRef.current) {
            actionRef.current.reload();
          }
          handleDeleteModalOpen(false);
        }}
      >
        Anda yakin ingin menghapus user ini?
      </Modal>
    </PageContainer>
  );
};

export default InformationPage;
